import React, { useEffect, useRef } from 'react';
import gsap, { ScrollTrigger } from "gsap/all";


function Playreel() {
  const parent = useRef(null);
  const videodiv = useRef(null)
  useEffect(()=>{
    gsap.registerPlugin(ScrollTrigger)
    gsap.to(videodiv.current,{
      scrollTrigger: {
        trigger:"parent.current",
        top:"0 0",
        pin: true,
        markers: true
      },
      width: "120%",
      height: "100"
    })
  })
  return (
    <div ref={parent} className='w-full h-screen overflow-hidden relative bg-black'>
      <div ref={videodiv} className='w-40 sm:w-96 overflow-hidden aspect-video bg-red-500 absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 '>
        <video muted loop autoPlay className='w-full h-full scale-[2.5] absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2' src="https://player.vimeo.com/progressive_redirect/playback/914803778/rendition/1080p/file.mp4?loc=external&log_user=0&signature=5344c0e4fea63ca54bb433621ca0be7b9470b475583fa68b26de2b6e380a390a"></video>
      </div>      
      <div className='overlap absolute  w-full h-full text-white flex flex-col justify-between py-20'>
        <div className='w-full flex items-center justify-center gap-3'>
            <svg
             viewBox="0 0 12 12" fill="none" xmlns="http://www.w3.org/2000/svg" className="w-4" data-v-669b4a84="">
              <path d="M7.41908 4.56679L6.13722 0L4.85418 4.92566L0 6L4.85418 7.25435L6.13722 12L7.3276 7.25435L12 6L7.41908 4.56679Z" fill="currentColor" data-v-669b4a84="">                
              </path>
            </svg>
            <h3 className='text:sm'>Work in Motion</h3>
        </div>
        <h1 className='w-full flex justify-evenly gap-52 sm:gap-[96] items-center'>
          <div className='text-4xl sm:text-8xl font-light'>Play</div>
          <div className='text-4xl sm:text-8xl font-light'>Reel</div>
        </h1>
        <p className='text-center px-10 text-xs'>Our work is best experienced in motion. Donâ€™t forget to put on your headphones.</p>
      </div> 
    </div>
  )
}

export default Playreel
